import React from "react";
import { useQuery } from "@apollo/client";
import { GET_QUOTE } from "../gql/queries";

const Home = () => {
  const { loading, error, data } = useQuery(GET_QUOTE);
  console.log(data);

  if (data.quotes.length === 0) return "No Quotes Available";

  return loading ? (
    <h1> Loading</h1>
  ) : error ? (
    <h1> Error </h1>
  ) : (
    <div>
      {data.quotes.map((quote) => (
        <blockquote key={quote.id}>
          <h5> {quote.title}</h5>
          <p> {quote.by} </p>
          <p> {quote.name} </p>
          <p> {quote.email} </p>
        </blockquote>
      ))}
    </div>
  );
};

export default Home;
